/**
 * Represents the season of the year.
 */
export type Season = 'spring'
| 'summer'
| 'fall'
| 'winter'
| 'all';

/**
 * Represents the weather conditions in the game.
 */
export type Weather = 'sunny'
| 'breezy-with-cherry-blossoms'
| 'breezy-with-falling-leaves'
| 'rain'
| 'rainy'
| 'storm'
| 'thunderstorm'
| 'snow'
| 'blizzard';
